package com.example.armenia;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.widget.ListView;

import java.util.ArrayList;


public class Tours extends AppCompatActivity {
    static ArrayList<ToursC> list = new ArrayList<>();
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_tours);

        RecyclerView recycle = findViewById(R.id.recycleID);
addTours();


      recycle.setLayoutManager(new LinearLayoutManager(this,LinearLayoutManager.VERTICAL,false));
        MyToursAdapter adapter = new MyToursAdapter(this, list );
        recycle.setAdapter(adapter);




    }

     private void addTours(){
        list.clear();
        list.add(new ToursC("One way tour","https://onewaytour.com/ru/","https://onewaytour.com/wp-content/uploads/2016/02/Logo-one-way-tour.png"));
        list.add(new ToursC("Armenia Travel","https://www.armeniatravel.am/en","data:image/jpeg;base64,"));
        list.add(new ToursC("Hyur Service","https://hyurservice.com/en","data:image/png;base64,"));
        list.add(new ToursC("Jan Armenia","https://janarmenia.com/","data:image/jpeg;base64,"));
        list.add(new ToursC("Fun Travel","https://funtravel.am/ru/","https://i.mycdn.me/i?r=AzE4eq1iDEEybY83tajERajMh7s0oDghy6GWrSjRMm1Z4zZA7271LYYFpjripFaGeQ8"));
     }

}

